package com.ibm.cio.cmr.request.util.external;

/**
 * Represents an LOV/Dropdown item
 * 
 * @author JeffZAMORA
 * 
 */
public class LovItem {

  private String id;
  private String label;

  public String getId() {
    return id;
  }

  public void setId(String id) {
    this.id = id;
  }

  public String getLabel() {
    return label;
  }

  public void setLabel(String label) {
    this.label = label;
  }

}
